import React, { Component } from "react";
import { Link } from "react-router-dom";
import request from 'superagent';


// layout
import LayoutAccount from "../../layout/Account";

// components
import SimpleHeading from "../../components/SimpleHeading";
import LoginForm from "../../components/SignUpForm";
process.env.NODE_TLS_REJECT_UNAUTHORIZED = '0';

class SignUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      width: null
    };
  }
  getFeaturedProducts = () => {
    request.get(`http://localhost:5000/api/klant/3`).then(function (response) {
      //     // handle success
          console.log(response.body);
         var repo = response.data;
      });

  };
  render() {
    return (
      <React.Fragment>
        <LayoutAccount className="SignUp" simple>
          <div className="wrapper">
            <SimpleHeading
              title="registreren"
              description="Vul hier u persoonsgegevens in om een account aan te maken"
            />
            <LoginForm />
            href={repo.body}
          </div>
        </LayoutAccount>
      </React.Fragment>
    );
  }
}
export default SignUp;
